import React from 'react';
import './App.css';
import Main from './Main';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      loading: true,
      person: null
    }
  }
  async componentDidMount() {
    const url = "https://api.randomuser.me/";
    const response = await fetch('url');
    const data = await response.json();
    this.setState({ questions: data.data[0], loading: false})
  }

  render() {
    return (
      <div id='main'>
        {this.state.loading ?
          <div>
            loading...
          </div> :
          <div>
            <Main
              question={this.state.questions.question1}
              type='checkbox'
            />
            <br></br>
            <Main
              question='Q2.This is the second question'
              type='TrueFalse'
            />
            </div>
        }
      </div>
    );
  }
}

export default App;
